	.fl-builder-content .fl-node-<?php echo $id; ?> .uabb-infobox-text {
		
		<?php if( $settings->subhead_font_size['small'] != '' ) : ?>
		font-size: <?php echo $settings->subhead_font_size['small']; ?>px;
