	<?php if ( $pos == 'left' || $pos == 'right' ) { ?>
		.fl-node-<?php echo $id; ?> .uabb-infobox {
			text-align: <?php echo $pos; ?>;
		}
		.fl-builder-content .fl-node-<?php echo $id; ?> .<?php echo $class; ?> .uabb-infobox-content{
			width: calc(100% - <?php echo $cal_width; ?>px);
