			$size['width'] = $temp_width;

			if( $this->settings->video_ratio == '16_9' ) {
				$size['height'] = ( ( $temp_width / 16 ) * 9 );
				
			}else {
				$size['height'] = ( ( $temp_width / 4 ) * 3 );
			}
			return $size;
		}
		return false;	
	}
}

