            ); ?>

 

            <span class="description"><?php echo wp_kses( $this->description, $allowed_html ); ?></span>

			<script language="javascript">

			( function( $ ) {

			  $('input[type=radio][name=_customize-radio-tesseract_footer_additional_content_right]').change(function() {

