	class Tesseract_Customize_Font_Control extends WP_Customize_Control {



		private $fonts = false;



		public function __construct($manager, $id, $args = array(), $options = array()) {



			$this->fonts = array(

				'Arial' => 'Arial',

				'Alef' => 'Alef',

				'Carme' => 'Carme',

				'Droid Sans' => 'Droid Sans',

				'Francois One' => 'Francois One',

				'Josefin Slab' => 'Josefin Slab',

				'Lobster' => 'Lobster',

				'Luckiest Guy' => 'Luckiest Guy',

				'Jockey One' => 'Jockey One',

				'Maven Pro' => 'Maven Pro',

				'Modern Antiqua' => 'Modern Antiqua',

				'Nobile' => 'Nobile',

				'Oswald' => 'Oswald',

				'Permanent Marker' => 'Permanent Marker',

				'Roboto' => 'Roboto',

				'Share' => 'Share',

				'Tahoma' => 'Tahoma',

				'Ubuntu' => 'Ubuntu',

				'Verdana' => 'Verdana');

			parent::__construct( $manager, $id, $args );



		}



		public function render_content() {



			if( !empty($this->fonts) ) :

            ?>

                <label>

                    <span class="customize-control-title"><?php echo esc_html( $this->label ); ?></span>

					<div class="customize-font-select-control">

						<select <?php $this->link(); ?>>

							<?php

								foreach ( $this->fonts as $k => $v )

								{

									printf('<option value="%s" %s>%s</option>', $k, selected($this->value(), $k, false), $v);

								}

							?>

						</select>

					</div>

				</label>



            <?php

			endif;

		}



	}



endif;



/*========================== CUSTOMIZER SANITIZE FUNCTIONS ==========================*/



function tesseract_sanitize_textarea_html( $value ) {



	$allowed = array(

		//container and button

		'p' => array(),

		'center' => array(),

		'em' => array(),

		'strong' => array(),

		'h1' => array(),

		'h2' => array(),

		'h3' => array(),

		'h4' => array(),

		'h5' => array(),

		'h6' => array(),

		'br'  => array(),

		'ul'  => array(),

		'li'  => array(),

		'img' => array(

			'src' => array(),

			'height' => array(),

			'width' => array(),

			'style' => array()

		 ),

		'div' => array(

			'style' => array()

		),

		'input'    	=> array(

			'height'	=> array(),

			'name'		=> array(),

			'size' 		=> array(),

			'type' 		=> array(),

			'value'		=> array(),

			'width' 	=> array(),

			'class' 	=> array(),

			'id'		=> array(),

			'style' 	=> array(),

			'img' => array(

				'src' => array(),

				'height' => array(),

				'width' => array(),

				'style' => array()

			)

		),

		'button'   	=> array(

			'name' 		=> array(),

			'type'		=> array(),

			'value' 	=> array(),

			'class' 	=> array(),

			'id'		=> array(),

			'style' 	=> array()

		),



		//links

		'a'     => array(

			'href' 	=> array(),

			'target'=> array(),

			'name' 	=> array(),

			'class' => array(),

			'id'	=> array(),

			'style' => array()

		)

	);



	$allowed_prot = array('http', 'https', 'mailto', 'news', 'irc', 'feed', 'tel');





	return wp_kses($value, $allowed, $allowed_prot);



	}



function tesseract_sanitize_checkbox( $value ) {



	if ( $value == 'yes') :

        return 'yes';

	else:

		return 'no';

	endif;

}



function tesseract_sanitize_radio( $value ) {



	if ( $value == 1) :

        return 1;

	else:

		return '';

	endif;

}



function tesseract_blog_sanitize_content( $value ) {if ( ! in_array( $value, array( 'excerpt', 'content', 'titleonly' ) ) ):

		$value = 'excerpt';

	endif;

